using System.Collections;
using System.Collections.Generic;
using System.Text.RegularExpressions;
using UnityEngine;


public static class TextureUtilities
{
    // TODO: probably better to load this from an actual png or something.
    // An empty png
    private const string DEFAULT_STRING_RAW = "data:image/png;base64,";

    private static Sprite DefaultSprite { get { return _defaultSprite ?? (_defaultSprite = ParseSpriteFromString(DEFAULT_STRING_RAW)); } }

    private static Sprite _defaultSprite = null;

    public static Sprite LoadTextureFromBase64(string rawPng)
    {
        Sprite sprite;
        if (rawPng.Length <= 36)
        {
            if (ImageRepository.FindImageFromKey(rawPng, out sprite))
            {
                return sprite;
            }
            return DefaultSprite;
        }

        return ParseSpriteFromString(rawPng);
    }

    private static Sprite ParseSpriteFromString(string rawPng)
    {
        var base64Data = Regex.Match(rawPng, @"data:image/(?<type>.+?),(?<data>.+)").Groups["data"].Value;
        byte[] pictureBytes = System.Convert.FromBase64String(base64Data);

        Texture2D tex = new Texture2D(1, 1);
        tex.LoadImage(pictureBytes);

        return Sprite.Create(tex, new Rect(0.0f, 0.0f, tex.width, tex.height), new Vector2(0.5f, 0.5f), 1f, 0, SpriteMeshType.FullRect);
    }
}
